export function parseBuyInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>,
): ParsedBuyInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 10) {
    throw new SolanaError(
      SOLANA_ERROR__PROGRAM_CLIENTS__INSUFFICIENT_ACCOUNT_METAS,
      {
        actualAccountMetas: instruction.accounts.length,
        expectedAccountMetas: 10,
      },
    );
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      buyer: getNextAccount(),
      seller: getNextAccount(),
      sellMint: getNextAccount(),
      buyMint: getNextAccount(),
      auction: getNextAccount(),
      auctionSellAta: getNextAccount(),
      buyerBuyAta: getNextAccount(),
      buyerSellAta: getNextAccount(),
      sellerBuyAta: getNextAccount(),
      tokenProgram: getNextAccount(),
    },
    data: getBuyInstructionDataDecoder().decode(instruction.data),
  };
}
